import wximport Forms_class FmSpdCntBase(Forms_.FmSpdCnt):    DlgShowing = None  # which dialog showing now    def __init__(self, fmMain, *args, **kwds):        Forms_.FmSpdCnt.__init__(self, *args, **kwds)        # 以下 event 在此 Bind 的話, child class 不會觸發, 原因不明        # self.Bind(wx.EVT_ACTIVATE, self.on_activate)        # self.Bind(wx.EVT_CHAR_HOOK, self.onKeyHook)    def fmShow(self, pos: wx.Point = None):        if pos:            self.SetPosition(pos)        self.Show()        self.SetFocus()        FmSpdCntBase.DlgShowing = self    def fmHide(self):        self.Hide()        FmSpdCntBase.DlgShowing = None        # self.fmMain.zlRep.SetFocus()